import React from "react";
import logo from "./banner.jpg";
import talent from "./talentshow.jpg";
import Jumbotron from "react-bootstrap/Jumbotron";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardDeck";
import Maps from "./maps";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import MapComponent from "./route";
import RoutComponent from "./route";

export default class Loggedin extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],
      lat:0,
      lng:0,
      dates: "",
      notes: ""
    };
    this.getData = this.getData.bind(this);
  }

  render() {
    const { data } = this.state
    return (
      <div>
        <Container>
          <Row>
            <div className="row mt-3">
              <Col>
                <Card
                  bg="info"
                  text="white"
                  style={{ width: "30rem", height: "35rem" }}
                >
                  <Card.Header>Welcome {data.email}!</Card.Header>
                  <Card.Body>
                    <Card.Title>Rider Information</Card.Title>
                    <Card.Text>
                      You have been assigned to:
                      {data.assigned}
                    </Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            </div>
            <Col>
              <RoutComponent
                lat="0"
                lng="0"
                zoom={8}
                origin={{lat: 0, lng: 0}}
                destination={{ lat: 35.7838484, lng: -78.67093919999999 }}
              />
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
  getData() {
    fetch("/api/getuser").then(res => res.json());
  }
  componentDidMount() {
    var that = this;
    var url = "http://localhost:3000/api/data";

    fetch("/api/getuser")
      .then(function(response) {
        if (response.status >= 400) {
          throw new Error("Bad response from server");
        }
        return response.json();
      })
      .then(function(data) {
        that.setState({ data: data});

      });
  }
}
